mapper.insert:
INSERT INTO student_question_score (
    exam_id, student_id, question_id, score_id,
    answer, score, status
) VALUES (
    #{examId}, #{studentId}, #{questionId}, #{scoreId},
    #{answer}, #{score}, #{status}
)

mapper.update:
UPDATE student_question_score
exam_id = #{examId}, student_id = #{studentId}, question_id = #{questionId}, score_id = #{scoreId}, answer = #{answer}, score = #{score}, status = #{status},
WHERE record_id = #{recordId}

mapper.select:
SELECT 
FROM student_question_score 
WHERE exam_id = #{examId} 
AND student_id = #{studentId}
ORDER BY record_id

mapper.update:
UPDATE student_question_score 
SET score = #{score}, 
    status = #{status}
WHERE record_id = #{recordId}

mapper.select:
SELECT CAST(AVG(score) AS DECIMAL(5,2))
FROM student_question_score
WHERE question_id = #{questionId}
AND exam_id = #{examId}

mapper.delete:
DELETE FROM student_question_score 
WHERE record_id IN

    #{recordId}
